    def hupscale(
        grid: Grid,
        factor: Integer
    ) -> Grid:
        """ upscale grid horizontally """
        g = tuple()
        for row in grid:
            r = tuple()
            for value in row:
                r = r + tuple(value for num in range(factor))
            g = g + (r,)
        return g


    def vupscale(
        grid: Grid,
        factor: Integer
    ) -> Grid:
        """ upscale grid vertically """
        g = tuple()
        for row in grid:
            g = g + tuple(row for num in range(factor))
        return g


    def upscale(
        element: Element,
        factor: Integer
    ) -> Element:
        """ upscale object or grid """
        if isinstance(element, tuple):
            g = tuple()
            for row in element:
                upscaled_row = tuple()
                for value in row:
                    upscaled_row = upscaled_row + tuple(value for num in range(factor))
                g = g + tuple(upscaled_row for num in range(factor))
            return g
        else:
            if len(element) == 0:
                return frozenset()
            di_inv, dj_inv = ulcorner(element)
            di, dj = (-di_inv, -dj_inv)
            normed_obj = shift(element, (di, dj))
            o = set()
            for value, (i, j) in normed_obj:
                for io in range(factor):
                    for jo in range(factor):
                        o.add((value, (i * factor + io, j * factor + jo)))
            return shift(frozenset(o), (di_inv, dj_inv))


    def downscale(
        grid: Grid,
        factor: Integer
    ) -> Grid:
        """ downscale grid """
        h, w = len(grid), len(grid[0])
        g = tuple()
        for i in range(h):
            r = tuple()
            for j in range(w):
                if j % factor == 0:
                    r = r + (grid[i][j],)
            g = g + (r, )
        h = len(g)
        dsg = tuple()
        for i in range(h):
            if i % factor == 0:
                dsg = dsg + (g[i],)
        return dsg


    def hconcat(
        a: Grid,
        b: Grid
    ) -> Grid:
