    def occurrences(
        grid: Grid,
        obj: Object
    ) -> Indices:
        """ locations of occurrences of object in grid """
        occs = set()
        normed = normalize(obj)
        h, w = len(grid), len(grid[0])
        oh, ow = shape(obj)
        h2, w2 = h - oh + 1, w - ow + 1
        for i in range(h2):
            for j in range(w2):
                occurs = True
                for v, (a, b) in shift(normed, (i, j)):
                    if not (0 <= a < h and 0 <= b < w and grid[a][b] == v):
                        occurs = False
                        break
                if occurs:
                    occs.add((i, j))
        return frozenset(occs)


    def frontiers(
