        return len(piece) == len(piece[0]) if isinstance(piece, tuple) else height(piece) * width(piece) == len(piece) and height(piece) == width(piece)


    def vline(
        patch: Patch
    ) -> Boolean:
        """ whether the piece forms a vertical line """
        return height(patch) == len(patch) and width(patch) == 1


    def hline(
        patch: Patch
    ) -> Boolean:
        """ whether the piece forms a horizontal line """
        return width(patch) == len(patch) and height(patch) == 1


    def hmatching(
        a: Patch,
        b: Patch
    ) -> Boolean:
        """ whether there exists a row for which both patches have cells """
        return len(set(i for i, j in toindices(a)) & set(i for i, j in toindices(b))) > 0


    def vmatching(
        a: Patch,
        b: Patch
    ) -> Boolean:
        """ whether there exists a column for which both patches have cells """
        return len(set(j for i, j in toindices(a)) & set(j for i, j in toindices(b))) > 0


    def manhattan(
        a: Patch,
        b: Patch
    ) -> Integer:
        """ closest manhattan distance between two patches """
        return min(abs(ai - bi) + abs(aj - bj) for ai, aj in toindices(a) for bi, bj in toindices(b))


    def adjacent(
        a: Patch,
        b: Patch
    ) -> Boolean:
        """ whether two patches are adjacent """
        return manhattan(a, b) == 1


    def bordering(
        patch: Patch,
        grid: Grid
    ) -> Boolean:
        """ whether a patch is adjacent to a grid border """
        return uppermost(patch) == 0 or leftmost(patch) == 0 or lowermost(patch) == len(grid) - 1 or rightmost(patch) == len(grid[0]) - 1


    def centerofmass(
        patch: Patch
    ) -> IntegerTuple:
        """ center of mass """
        return tuple(map(lambda x: sum(x) // len(patch), zip(*toindices(patch))))


    def palette(
        element: Element
    ) -> IntegerSet:
        """ colors occurring in object or grid """
        if isinstance(element, tuple):
            return frozenset({v for r in element for v in r})
        return frozenset({v for v, _ in element})


    def numcolors(
        element: Element
    ) -> IntegerSet:
        """ number of colors occurring in object or grid """
        return len(palette(element))


    def color(
        obj: Object
    ) -> Integer:
        """ color of object """
        return next(iter(obj))[0]


    def toobject(
        patch: Patch,
        grid: Grid
    ) -> Object:
        """ object from patch and grid """
        h, w = len(grid), len(grid[0])
        return frozenset((grid[i][j], (i, j)) for i, j in toindices(patch) if 0 <= i < h and 0 <= j < w)


    def asobject(
        grid: Grid
    ) -> Object:
        """ conversion of grid to object """
        return frozenset((v, (i, j)) for i, r in enumerate(grid) for j, v in enumerate(r))


    def rot90(
        grid: Grid
    ) -> Grid:
        """ quarter clockwise rotation """
        return tuple(row for row in zip(*grid[::-1]))


    def rot180(
        grid: Grid
    ) -> Grid:
        """ half rotation """
        return tuple(tuple(row[::-1]) for row in grid[::-1])


    def rot270(
        grid: Grid
    ) -> Grid:
        """ quarter anticlockwise rotation """
        return tuple(tuple(row[::-1]) for row in zip(*grid[::-1]))[::-1]


    def hmirror(
        piece: Piece
    ) -> Piece:
        """ mirroring along horizontal """
        if isinstance(piece, tuple):
            return piece[::-1]
        d = ulcorner(piece)[0] + lrcorner(piece)[0]
        if isinstance(next(iter(piece))[1], tuple):
            return frozenset((v, (d - i, j)) for v, (i, j) in piece)
        return frozenset((d - i, j) for i, j in piece)


    def vmirror(
        piece: Piece
    ) -> Piece:
        """ mirroring along vertical """
        if isinstance(piece, tuple):
            return tuple(row[::-1] for row in piece)
        d = ulcorner(piece)[1] + lrcorner(piece)[1]
        if isinstance(next(iter(piece))[1], tuple):
            return frozenset((v, (i, d - j)) for v, (i, j) in piece)
        return frozenset((i, d - j) for i, j in piece)


    def dmirror(
        piece: Piece
    ) -> Piece:
        """ mirroring along diagonal """
        if isinstance(piece, tuple):
            return tuple(zip(*piece))
        a, b = ulcorner(piece)
        if isinstance(next(iter(piece))[1], tuple):
            return frozenset((v, (j - b + a, i - a + b)) for v, (i, j) in piece)
        return frozenset((j - b + a, i - a + b) for i, j in piece)


    def cmirror(
        piece: Piece
    ) -> Piece:
        """ mirroring along counterdiagonal """
        if isinstance(piece, tuple):
            return tuple(zip(*(r[::-1] for r in piece[::-1])))
        return vmirror(dmirror(vmirror(piece)))


    def fill(
        grid: Grid,
        value: Integer,
        patch: Patch
    ) -> Grid:
        """ fill value at indices """
        h, w = len(grid), len(grid[0])
        grid_filled = list(list(row) for row in grid)
        for i, j in toindices(patch):
            if 0 <= i < h and 0 <= j < w:
                grid_filled[i][j] = value
        return tuple(tuple(row) for row in grid_filled)


    def paint(
        grid: Grid,
        obj: Object
    ) -> Grid:
        """ paint object to grid """
        h, w = len(grid), len(grid[0])
        grid_painted = list(list(row) for row in grid)
        for value, (i, j) in obj:
            if 0 <= i < h and 0 <= j < w:
                grid_painted[i][j] = value
        return tuple(tuple(row) for row in grid_painted)


    def underfill(
        grid: Grid,
        value: Integer,
        patch: Patch
    ) -> Grid:
        """ fill value at indices that are background """
        h, w = len(grid), len(grid[0])
        bg = mostcolor(grid)
        g = list(list(r) for r in grid)
        for i, j in toindices(patch):
            if 0 <= i < h and 0 <= j < w:
                if g[i][j] == bg:
                    g[i][j] = value
        return tuple(tuple(r) for r in g)


    def underpaint(
        grid: Grid,
        obj: Object
    ) -> Grid:
        """ paint object to grid where there is background """
        h, w = len(grid), len(grid[0])
        bg = mostcolor(grid)
        g = list(list(r) for r in grid)
        for value, (i, j) in obj:
            if 0 <= i < h and 0 <= j < w:
                if g[i][j] == bg:
                    g[i][j] = value
        return tuple(tuple(r) for r in g)


    def hupscale(
        grid: Grid,
        factor: Integer
    ) -> Grid:
        """ upscale grid horizontally """
        g = tuple()
        for row in grid:
            r = tuple()
            for value in row:
                r = r + tuple(value for num in range(factor))
            g = g + (r,)
        return g


    def vupscale(
        grid: Grid,
        factor: Integer
    ) -> Grid:
        """ upscale grid vertically """
        g = tuple()
        for row in grid:
            g = g + tuple(row for num in range(factor))
        return g


    def upscale(
        element: Element,
        factor: Integer
    ) -> Element:
        """ upscale object or grid """
        if isinstance(element, tuple):
            g = tuple()
            for row in element:
                upscaled_row = tuple()
                for value in row:
                    upscaled_row = upscaled_row + tuple(value for num in range(factor))
                g = g + tuple(upscaled_row for num in range(factor))
            return g
        else:
            if len(element) == 0:
                return frozenset()
            di_inv, dj_inv = ulcorner(element)
            di, dj = (-di_inv, -dj_inv)
            normed_obj = shift(element, (di, dj))
            o = set()
            for value, (i, j) in normed_obj:
                for io in range(factor):
                    for jo in range(factor):
                        o.add((value, (i * factor + io, j * factor + jo)))
            return shift(frozenset(o), (di_inv, dj_inv))


    def downscale(
        grid: Grid,
        factor: Integer
    ) -> Grid:
        """ downscale grid """
        h, w = len(grid), len(grid[0])
        g = tuple()
        for i in range(h):
            r = tuple()
            for j in range(w):
                if j % factor == 0:
                    r = r + (grid[i][j],)
            g = g + (r, )
        h = len(g)
        dsg = tuple()
        for i in range(h):
            if i % factor == 0:
                dsg = dsg + (g[i],)
        return dsg


    def hconcat(
        a: Grid,
        b: Grid
    ) -> Grid:
        """ concatenate two grids horizontally """
        return tuple(i + j for i, j in zip(a, b))


    def vconcat(
        a: Grid,
        b: Grid
    ) -> Grid:
        """ concatenate two grids vertically """
        return a + b


    def subgrid(
        patch: Patch,
        grid: Grid
    ) -> Grid:
        """ smallest subgrid containing object """
        return crop(grid, ulcorner(patch), shape(patch))


    def hsplit(
        grid: Grid,
        n: Integer
    ) -> Tuple:
        """ split grid horizontally """
        h, w = len(grid), len(grid[0]) // n
        offset = len(grid[0]) % n != 0
        return tuple(crop(grid, (0, w * i + i * offset), (h, w)) for i in range(n))


    def vsplit(
        grid: Grid,
        n: Integer
    ) -> Tuple:
        """ split grid vertically """
        h, w = len(grid) // n, len(grid[0])
        offset = len(grid) % n != 0
        return tuple(crop(grid, (h * i + i * offset, 0), (h, w)) for i in range(n))


    def cellwise(
        a: Grid,
        b: Grid,
        fallback: Integer
    ) -> Grid:
        """ cellwise match of two grids """
        h, w = len(a), len(a[0])
        resulting_grid = tuple()
        for i in range(h):
            row = tuple()
            for j in range(w):
                a_value = a[i][j]
                value = a_value if a_value == b[i][j] else fallback
                row = row + (value,)
            resulting_grid = resulting_grid + (row, )
        return resulting_grid


    def replace(
        grid: Grid,
        replacee: Integer,
        replacer: Integer
    ) -> Grid:
        """ color substitution """
        return tuple(tuple(replacer if v == replacee else v for v in r) for r in grid)


    def switch(
        grid: Grid,
        a: Integer,
        b: Integer
    ) -> Grid:
        """ color switching """
        return tuple(tuple(v if (v != a and v != b) else {a: b, b: a}[v] for v in r) for r in grid)


    def center(
        patch: Patch
    ) -> IntegerTuple:
        """ center of the patch """
        return (uppermost(patch) + height(patch) // 2, leftmost(patch) + width(patch) // 2)


    def position(
        a: Patch,
        b: Patch
    ) -> IntegerTuple:
        """ relative position between two patches """
        ia, ja = center(toindices(a))
        ib, jb = center(toindices(b))
        if ia == ib:
            return (0, 1 if ja < jb else -1)
        elif ja == jb:
            return (1 if ia < ib else -1, 0)
        elif ia < ib:
            return (1, 1 if ja < jb else -1)
        elif ia > ib:
            return (-1, 1 if ja < jb else -1)


    def index(
        grid: Grid,
        loc: IntegerTuple
    ) -> Integer:
        """ color at location """
        i, j = loc
        h, w = len(grid), len(grid[0])
        if not (0 <= i < h and 0 <= j < w):
            return None
        return grid[loc[0]][loc[1]] 


    def canvas(
        value: Integer,
        dimensions: IntegerTuple
    ) -> Grid:
        """ grid construction """
        return tuple(tuple(value for j in range(dimensions[1])) for i in range(dimensions[0]))


    def corners(
        patch: Patch
    ) -> Indices:
        """ indices of corners """
        return frozenset({ulcorner(patch), urcorner(patch), llcorner(patch), lrcorner(patch)})


    def connect(
        a: IntegerTuple,
        b: IntegerTuple
    ) -> Indices:
        """ line between two points """
        ai, aj = a
        bi, bj = b
        si = min(ai, bi)
        ei = max(ai, bi) + 1
        sj = min(aj, bj)
        ej = max(aj, bj) + 1
        if ai == bi:
            return frozenset((ai, j) for j in range(sj, ej))
        elif aj == bj:
            return frozenset((i, aj) for i in range(si, ei))
        elif bi - ai == bj - aj:
            return frozenset((i, j) for i, j in zip(range(si, ei), range(sj, ej)))
        elif bi - ai == aj - bj:
            return frozenset((i, j) for i, j in zip(range(si, ei), range(ej - 1, sj - 1, -1)))
        return frozenset()


    def cover(
        grid: Grid,
        patch: Patch
    ) -> Grid:
        """ remove object from grid """
        return fill(grid, mostcolor(grid), toindices(patch))


    def trim(
        grid: Grid
    ) -> Grid:
        """ trim border of grid """
        return tuple(r[1:-1] for r in grid[1:-1])


    def move(
        grid: Grid,
        obj: Object,
        offset: IntegerTuple
    ) -> Grid:
        """ move object on grid """
        return paint(cover(grid, obj), shift(obj, offset))


    def tophalf(
        grid: Grid
    ) -> Grid:
        """ upper half of grid """
        return grid[:len(grid) // 2]


    def bottomhalf(
        grid: Grid
    ) -> Grid:
        """ lower half of grid """
        return grid[len(grid) // 2 + len(grid) % 2:]


    def lefthalf(
        grid: Grid
    ) -> Grid:
        """ left half of grid """
        return rot270(tophalf(rot90(grid)))


    def righthalf(
        grid: Grid
    ) -> Grid:
        """ right half of grid """
        return rot270(bottomhalf(rot90(grid)))


    def vfrontier(
        location: IntegerTuple
    ) -> Indices:
        """ vertical frontier """
        return frozenset((i, location[1]) for i in range(30))


    def hfrontier(
        location: IntegerTuple
    ) -> Indices:
        """ horizontal frontier """
        return frozenset((location[0], j) for j in range(30))


    def backdrop(
        patch: Patch
    ) -> Indices:
        """ indices in bounding box of patch """
        if len(patch) == 0:
            return frozenset({})
        indices = toindices(patch)
        si, sj = ulcorner(indices)
        ei, ej = lrcorner(patch)
        return frozenset((i, j) for i in range(si, ei + 1) for j in range(sj, ej + 1))


    def delta(
        patch: Patch
    ) -> Indices:
        """ indices in bounding box but not part of patch """
        if len(patch) == 0:
            return frozenset({})
        return backdrop(patch) - toindices(patch)


    def gravitate(
        source: Patch,
        destination: Patch
    ) -> IntegerTuple:
        """ direction to move source until adjacent to destination """
        si, sj = center(source)
        di, dj = center(destination)
        i, j = 0, 0
        if vmatching(source, destination):
            i = 1 if si < di else -1
        else:
            j = 1 if sj < dj else -1
        gi, gj = i, j
        c = 0
        while not adjacent(source, destination) and c < 42:
            c += 1
            gi += i
            gj += j
            source = shift(source, (i, j))
        return (gi - i, gj - j)


    def inbox(
        patch: Patch
    ) -> Indices:
        """ inbox for patch """
        ai, aj = uppermost(patch) + 1, leftmost(patch) + 1
        bi, bj = lowermost(patch) - 1, rightmost(patch) - 1
        si, sj = min(ai, bi), min(aj, bj)
        ei, ej = max(ai, bi), max(aj, bj)
        vlines = {(i, sj) for i in range(si, ei + 1)} | {(i, ej) for i in range(si, ei + 1)}
        hlines = {(si, j) for j in range(sj, ej + 1)} | {(ei, j) for j in range(sj, ej + 1)}
        return frozenset(vlines | hlines)


    def outbox(
        patch: Patch
    ) -> Indices:
        """ outbox for patch """
        ai, aj = uppermost(patch) - 1, leftmost(patch) - 1
        bi, bj = lowermost(patch) + 1, rightmost(patch) + 1
        si, sj = min(ai, bi), min(aj, bj)
        ei, ej = max(ai, bi), max(aj, bj)
        vlines = {(i, sj) for i in range(si, ei + 1)} | {(i, ej) for i in range(si, ei + 1)}
        hlines = {(si, j) for j in range(sj, ej + 1)} | {(ei, j) for j in range(sj, ej + 1)}
        return frozenset(vlines | hlines)


    def box(
        patch: Patch
    ) -> Indices:
        """ outline of patch """
        if len(patch) == 0:
            return patch
        ai, aj = ulcorner(patch)
        bi, bj = lrcorner(patch)
        si, sj = min(ai, bi), min(aj, bj)
        ei, ej = max(ai, bi), max(aj, bj)
        vlines = {(i, sj) for i in range(si, ei + 1)} | {(i, ej) for i in range(si, ei + 1)}
        hlines = {(si, j) for j in range(sj, ej + 1)} | {(ei, j) for j in range(sj, ej + 1)}
        return frozenset(vlines | hlines)


    def shoot(
        start: IntegerTuple,
        direction: IntegerTuple
    ) -> Indices:
        """ line from starting point and direction """
        return connect(start, (start[0] + 42 * direction[0], start[1] + 42 * direction[1]))


    def occurrences(
        grid: Grid,
        obj: Object
    ) -> Indices:
        """ locations of occurrences of object in grid """
        occs = set()
        normed = normalize(obj)
        h, w = len(grid), len(grid[0])
        oh, ow = shape(obj)
        h2, w2 = h - oh + 1, w - ow + 1
        for i in range(h2):
            for j in range(w2):
                occurs = True
                for v, (a, b) in shift(normed, (i, j)):
                    if not (0 <= a < h and 0 <= b < w and grid[a][b] == v):
                        occurs = False
                        break
                if occurs:
                    occs.add((i, j))
        return frozenset(occs)


    def frontiers(
        grid: Grid
    ) -> Objects:
        """ set of frontiers """
        h, w = len(grid), len(grid[0])
        row_indices = tuple(i for i, r in enumerate(grid) if len(set(r)) == 1)
        column_indices = tuple(j for j, c in enumerate(dmirror(grid)) if len(set(c)) == 1)
        hfrontiers = frozenset({frozenset({(grid[i][j], (i, j)) for j in range(w)}) for i in row_indices})
        vfrontiers = frozenset({frozenset({(grid[i][j], (i, j)) for i in range(h)}) for j in column_indices})
        return hfrontiers | vfrontiers


    def compress(
        grid: Grid
    ) -> Grid:
        """ removes frontiers from grid """
        ri = tuple(i for i, r in enumerate(grid) if len(set(r)) == 1)
        ci = tuple(j for j, c in enumerate(dmirror(grid)) if len(set(c)) == 1)
        return tuple(tuple(v for j, v in enumerate(r) if j not in ci) for i, r in enumerate(grid) if i not in ri)


    def hperiod(
        obj: Object
    ) -> Integer:
        """ horizontal periodicity """
        normalized = normalize(obj)
        w = width(normalized)
        for p in range(1, w):
            offsetted = shift(normalized, (0, -p))
            pruned = frozenset({(c, (i, j)) for c, (i, j) in offsetted if j >= 0})
            if pruned.issubset(normalized):
                return p
        return w


    def vperiod(
        obj: Object
    ) -> Integer:
        """ vertical periodicity """
        normalized = normalize(obj)
        h = height(normalized)
        for p in range(1, h):
            offsetted = shift(normalized, (-p, 0))
            pruned = frozenset({(c, (i, j)) for c, (i, j) in offsetted if i >= 0})
            if pruned.issubset(normalized):
                return p
        return h

