    from arc_types import *


    def identity(
        x: Any
    ) -> Any:
        """ identity function """
        return x


    def add(
        a: Numerical,
        b: Numerical
    ) -> Numerical:
        """ addition """
        if isinstance(a, int) and isinstance(b, int):
            return a + b
        elif isinstance(a, tuple) and isinstance(b, tuple):
            return (a[0] + b[0], a[1] + b[1])
        elif isinstance(a, int) and isinstance(b, tuple):
            return (a + b[0], a + b[1])
        return (a[0] + b, a[1] + b)


    def subtract(
        a: Numerical,
        b: Numerical
    ) -> Numerical:
        """ subtraction """
        if isinstance(a, int) and isinstance(b, int):
            return a - b
        elif isinstance(a, tuple) and isinstance(b, tuple):
            return (a[0] - b[0], a[1] - b[1])
        elif isinstance(a, int) and isinstance(b, tuple):
            return (a - b[0], a - b[1])
        return (a[0] - b, a[1] - b)


    def multiply(
        a: Numerical,
        b: Numerical
    ) -> Numerical:
        """ multiplication """
        if isinstance(a, int) and isinstance(b, int):
            return a * b
        elif isinstance(a, tuple) and isinstance(b, tuple):
            return (a[0] * b[0], a[1] * b[1])
        elif isinstance(a, int) and isinstance(b, tuple):
            return (a * b[0], a * b[1])
        return (a[0] * b, a[1] * b)


    def divide(
        a: Numerical,
        b: Numerical
    ) -> Numerical:
        """ floor division """
        if isinstance(a, int) and isinstance(b, int):
            return a // b
        elif isinstance(a, tuple) and isinstance(b, tuple):
            return (a[0] // b[0], a[1] // b[1])
        elif isinstance(a, int) and isinstance(b, tuple):
            return (a // b[0], a // b[1])
        return (a[0] // b, a[1] // b)


    def invert(
        n: Numerical
    ) -> Numerical:
        """ inversion with respect to addition """
        return -n if isinstance(n, int) else (-n[0], -n[1])


    def even(
        n: Integer
    ) -> Boolean:
        """ evenness """
        return n % 2 == 0


    def double(
        n: Numerical
    ) -> Numerical:
        """ scaling by two """
        return n * 2 if isinstance(n, int) else (n[0] * 2, n[1] * 2)


    def halve(
        n: Numerical
    ) -> Numerical:
        """ scaling by one half """
        return n // 2 if isinstance(n, int) else (n[0] // 2, n[1] // 2)


    def flip(
        b: Boolean
    ) -> Boolean:
        """ logical not """
        return not b


    def equality(
        a: Any,
        b: Any
    ) -> Boolean:
        """ equality """
        return a == b


    def contained(
        value: Any,
        container: Container
    ) -> Boolean:
        """ element of """
        return value in container


    def combine(
        a: Container,
        b: Container
    ) -> Container:
        """ union """
        return type(a)((*a, *b))


    def intersection(
        a: FrozenSet,
        b: FrozenSet
    ) -> FrozenSet:
        """ returns the intersection of two containers """
        return a & b


    def difference(
        a: FrozenSet,
        b: FrozenSet
    ) -> FrozenSet:
        """ set difference """
        return type(a)(e for e in a if e not in b)


    def dedupe(
        tup: Tuple
    ) -> Tuple:
        """ remove duplicates """
        return tuple(e for i, e in enumerate(tup) if tup.index(e) == i)


    def order(
        container: Container,
        compfunc: Callable
    ) -> Tuple:
        """ order container by custom key """
        return tuple(sorted(container, key=compfunc))


    def repeat(
        item: Any,
        num: Integer
    ) -> Tuple:
        """ repetition of item within vector """
        return tuple(item for i in range(num))


    def greater(
        a: Integer,
        b: Integer
    ) -> Boolean:
        """ greater """
        return a > b


    def size(
        container: Container
    ) -> Integer:
        """ cardinality """
        return len(container)


    def merge(
        containers: ContainerContainer
    ) -> Container:
        """ merging """
        return type(containers)(e for c in containers for e in c)


    def maximum(
        container: IntegerSet
    ) -> Integer:
        """ maximum """
        return max(container, default=0)


    def minimum(
        container: IntegerSet
    ) -> Integer:
        """ minimum """
        return min(container, default=0)


    def valmax(
        container: Container,
        compfunc: Callable
    ) -> Integer:
        """ maximum by custom function """
        return compfunc(max(container, key=compfunc, default=0))


    def valmin(
        container: Container,
        compfunc: Callable
    ) -> Integer:
        """ minimum by custom function """
        return compfunc(min(container, key=compfunc, default=0))


    def argmax(
        container: Container,
        compfunc: Callable
    ) -> Any:
        """ largest item by custom order """
        return max(container, key=compfunc)


    def argmin(
        container: Container,
        compfunc: Callable
    ) -> Any:
        """ smallest item by custom order """
        return min(container, key=compfunc)


    def mostcommon(
        container: Container
    ) -> Any:
        """ most common item """
        return max(set(container), key=container.count)


    def leastcommon(
        container: Container
    ) -> Any:
        """ least common item """
        return min(set(container), key=container.count)


    def initset(
        value: Any
    ) -> FrozenSet:
        """ initialize container """
        return frozenset({value})


    def both(
        a: Boolean,
        b: Boolean
    ) -> Boolean:
        """ logical and """
        return a and b


    def either(
        a: Boolean,
        b: Boolean
